L.Control.MousePosition = L.Control.extend({
    options: {
        position: 'bottomleft',
        separator: ' : ',
        emptyString: 'Unavailable',
        lngFirst: false,
        numDigits: 5,
        lngFormatter: undefined,
        latFormatter: undefined,
        prefix: ""
    },
    onAdd: function(map) {
        this._container = L.DomUtil.create('div', 'leaflet-control-mouseposition');
        L.DomEvent.disableClickPropagation(this._container);
        map.on('mousemove', this._onMouseMove, this);
        this._map=map;
        this._container.innerHTML = this.options.emptyString;
        return this._container;
    },
    onRemove: function(map) {
        map.off('mousemove', this._onMouseMove)
    },
    _onMouseMove: function(e) {
        var lng = this.options.lngFormatter ? this.options.lngFormatter(e.latlng.lng) : L.Util.formatNum(e.latlng.lng, this.options.numDigits);
        var lat = this.options.latFormatter ? this.options.latFormatter(e.latlng.lat) : L.Util.formatNum(e.latlng.lat, this.options.numDigits);
        var value = this.options.lngFirst ? lng + this.options.separator + lat : lat + this.options.separator + lng;
        var prefixAndValue = this.options.prefix + ' ' + value;
        this._container.innerHTML = prefixAndValue;
    }

});

L.Map.mergeOptions({
    positionControl: false
});

L.Map.addInitHook(function() {
    if (this.options.positionControl) {
        this.positionControl = new L.Control.MousePosition();
        this.addControl(this.positionControl);
    }
});

L.control.mousePosition = function(options) {
    return new L.Control.MousePosition(options);
};

L.Control.Sidebar = L.Control.extend({
    includes: L.Mixin.Events,
    options: {
        closeButton: true,
        position: 'left',
        autoPan: true,
    },
    initialize: function(placeholder, options) {
        L.setOptions(this, options);

        this._control = {};
        this._sidebar = $(placeholder);
        this._current = $();
        this._currentButton = $();
   
        // Find content container
        var content = this._contentContainer = L.DomUtil.get(placeholder);

        // Remove the content container from its original parent
    
        
        content.parentNode.removeChild(content);
 
        var l = 'leaflet-';

        // Create sidebar container
        var container = this._container =
                L.DomUtil.create('div', l + 'sidebar ' + this.options.position);

        // Style and attach content container
        L.DomUtil.addClass(content, l + 'control');
        
        
        
        container.appendChild(content);
        
 
        // Create close button and attach it if configured
        if (this.options.closeButton) {
            var close = this._closeButton =
                    L.DomUtil.create('a', 'close', container);
            close.innerHTML = '&times;';
        }
    },
    addTo: function(map) {
        
        
        var container = this._container;
        var content = this._contentContainer;
        // Attach event to close button
        if (this.options.closeButton) {
            var close = this._closeButton;

            L.DomEvent.on(close, 'click', this.hide, this);
        }
        // Attach sidebar container to controls container
        var controlContainer = map._controlContainer;
       controlContainer.insertBefore(container, controlContainer.firstChild);
 
        this._map = map;
       // Make sure we don't drag the map when we interact with the content
        var stop = L.DomEvent.stopPropagation;
        L.DomEvent
                .on(content, 'click', stop)
                .on(content, 'mousedown', stop)
                .on(content, 'touchstart', stop)
                .on(content, 'dblclick', stop)
                .on(content, 'mousewheel', stop)
                .on(content, 'MozMousePixelScroll', stop);
        return this;
    },
    addPane: function(pane) {
        pane
         .hide()
         .appendTo(this._contentContainer);

    },
    removeFrom: function(map) {
        //if the control is visible, hide it before removing it.
        this.hide();

        var content = this._contentContainer;

        // Remove sidebar container from controls container
        var controlContainer = map._controlContainer;
        controlContainer.removeChild(this._container);

        //disassociate the map object
        this._map = null;

        // Unregister events to prevent memory leak
        var stop = L.DomEvent.stopPropagation;
        L.DomEvent
                .off(content, 'click', stop)
                .off(content, 'mousedown', stop)
                .off(content, 'touchstart', stop)
                .off(content, 'dblclick', stop)
                .off(content, 'mousewheel', stop)
                .off(content, 'MozMousePixelScroll', stop);

        if (this._closeButton && this._close) {
            var close = this._closeButton;

            L.DomEvent.off(close, 'click', this.hide, this);
        }

        return this;
    },
    isVisible: function() {
        return L.DomUtil.hasClass(this._container, 'visible');
    },
    show: function() {
        if (!this.isVisible()) {
            L.DomUtil.addClass(this._container, 'visible');
            if (this.options.autoPan) {
                this._map.panBy([-this.getOffset() / 2, 0], {
                    duration: 0.5
                });
            }
            this.fire('show');
        }
    },
    hide: function(e) {
        if (this.isVisible()) {
            L.DomUtil.removeClass(this._container, 'visible');
            if (this.options.autoPan) {
                this._map.panBy([this.getOffset() / 2, 0], {
                    duration: 0.5
                });
            }
            this.fire('hide');
           
            this._current
                    .hide()
                    .trigger('hide');
            this._currentButton
                    .removeClass('active');
            this._current = this._currentButton = $();
            
        }
        if (e) {
            L.DomEvent.stopPropagation(e);
        }
    },
    toggle: function() {
        if (this.isVisible()) {
            this.hide();
           

        } else {
            this.show();
        }
    },
    togglePane: function(pane, button) {

        this._current
                .hide()
                .trigger('hide');

        this._currentButton
                .removeClass('active');

        if (this._current === pane) {
            
            this.hide();
            $(this._sidebar).hide();
            
            this._current = this._currentButton = $();
        } else {
            $(this._sidebar).show();
            this.show();
            this._current = pane;
            this._currentButton = button || $();
        }

        //   map.invalidateSize({pan: false, animate: false});

        this._current
                .show()
                .trigger('show');

        this._currentButton
                .addClass('active');


    },
//  control.togglePane: function(pane, button) {
//    current
//      .hide()
//      .trigger('hide');
//
//    currentButton
//      .removeClass('active');
//
//    if (current === pane) {
//      $(sidebar).hide();
//      current = currentButton = $();
//    } else {
//      $(sidebar).show();
//      current = pane;
//      currentButton = button || $();
//    }
//
//    map.invalidateSize({pan: false, animate: false});
//
//    current
//      .show()
//      .trigger('show');
//
//    currentButton
//      .addClass('active');
//    };
//
//        return control;
//    };
    getContainer: function() {
        return this._contentContainer;
    },
    getCloseButton: function() {
        return this._closeButton;
    },
    setContent: function(content) {
        this.getContainer().innerHTML = content;
        return this;
    },
    getOffset: function() {
        if (this.options.position === 'right') {
            return -this._container.offsetWidth;
        } else {
            return this._container.offsetWidth;
        }
    }
});

